const passport = require("passport");
const tokenObject = require("../auth/tokens");
const User = require("../models").User;

module.exports = {
  local: (req, res, next) => {
    passport.authenticate("local", { session: false }, (error, user, info) => {
      if (error) {
        return res.status(401).json(error);
      }
      req.user = user;
      return next();
    })(req, res, next);
  },
  refresh: async (req, res, next) => {
    try {
      const { refreshToken } = req.body;
      const id = await tokenObject.refresh.search(refreshToken);

      if (!id) {
        return res.status(401).json({ message: "Refresh token expired" });
      }

      await tokenObject.refresh.delete(refreshToken);
      const user = await User.findOne({
        where: { id },
      });

      req.user = user;
      return next();
    } catch (error) {
      console.log(error);
      return res.status(500).json(error);
    }
  },
  bearer: (req, res, next) => {
    passport.authenticate("bearer", { session: false }, (error, user, info) => {
      if (error) {
        return res.status(401).json(error);
      }

      req.token = info.token;
      req.user = user;
      return next();
    })(req, res, next);
  },
};

const passport = require("passport");
const User = require("../models").User;
const LocalStrategy = require("passport-local").Strategy;
const BearerStrategy = require("passport-http-bearer").Strategy;
const bcrypt = require("bcrypt");
const jwt = require("jsonwebtoken");

passport.use(
  new LocalStrategy(async function (username, password, done) {
    const user = await User.findOne({
      where: { username },
    });

    if (!user) {
      done("This user doesnt exists", null);
    }

    const verifyPassword = await bcrypt.compare(password, user.password);

    if (!verifyPassword) {
      done("Invalid username or password", null);
    }

    return done(null, user);
  })
);

passport.use(
  new BearerStrategy(async function (token, done) {
    try {
      const id = jwt.verify(token, process.env.JWT_KEY);
      const user = await User.findOne({
        where: { id },
      });

      return done(null, user);
    } catch (error) {
      console.log("ERROR", error);
      done(error, null);
    }
  })
);

const jwt = require("jsonwebtoken");
const crypto = require("crypto");
const moment = require("moment");
const allowList = require("../redis/allowlist-refresh-token");
const ACCESS_EXPIRES_IN = "15m";
const REFRESH_EXPIRES_IN = "5d";

module.exports = {
  access: {
    create(id) {
      const payload = { id };
      return jwt.sign(payload, process.env.JWT_KEY, {
        expiresIn: ACCESS_EXPIRES_IN,
      });
    },
  },
  refresh: {
    async create(id) {
      const token = crypto.randomBytes(24).toString("hex");
      const expires_at = moment().add("5", "d").unix();
      await allowList.insert(token, id, expires_at);
      return token;
    },
    async search(refreshToken) {
      const id = await allowList.search(refreshToken);
      return id;
    },
    async delete(refreshToken) {
      await allowList.delete(refreshToken);
    },
  },
};

